import { InputWithValidate } from "./InputWithValidate"
import { useEffect, useState } from "react";
import "../style.css"
import { BasicSelect } from "./BasicSelect";
import { useSelector } from "react-redux";
import { Button, Checkbox, FormControlLabel, TextField } from "@mui/material";
import { SelectFromJson } from "./SelectFromJson";



export const InputCarDetailes = ({
    licensePlate= null,
    company=null,
    model=null,
    numberOfSeats= null,
    // img: "Toyota_Corolla-Space.png",
    yearBook= null,
    automaticGear= false,
    driveTypeId= null,
    pricePerHour= null,
    fuelConsumptionPerKm= null,
    balance= null,
    city= null,
    street= null})=>{

    let carModels = useSelector(x => x.CarModels)
    let driveTypes = useSelector(x => x.DriveTypes)

    let carsDictionary = {}
    carModels.forEach(element => {
        if (!carsDictionary[element.company])
            carsDictionary[element.company] = [element.model]
        else
            carsDictionary[element.company].push(element.model)
    });


    const [Models, SetModels] = useState([])
    const [Company, SetCompany] = useState()
    const [DriveType, setDriveType] = useState()

    useEffect(() => {
        if (!Company) {
            SetModels([])
            return
        }
        SetModels(carsDictionary[Company])

    }, [Company])

    /*
    { 
        id: 1,
        licensePlate: "36-375-97",
        carModelId: 1,
        numberOfSeats: 5,
        img: "Toyota_Corolla-Space.png",
        yearBook: 2017,
        automaticGear: true,
        driveTypeId: 2,
        pricePerHour: 30,
        fuelConsumptionPerKm: 12,
        balance: 30,
        city: "ירושלים",
        street: "שטראוס 25",
        available: false },
    */

    let licensePlateConditions = [
        { func: (value) => !(/^\d{7,8}$/.test(value) || value == ""), messege: "מספר רישוי לא תקין" }
    ]

    let yearBookConditions = [
        { func: (value) => !(/^\d*$/.test(value) || value == ""), messege: "הכנס מספרים בלבד" },
        { func: (value) => !(value == "" || parseInt(value) <= new Date().getFullYear()), messege: "לא ניתן להכניס שנתון שיחול בעתיד" }
    ]

    return <>
        <InputWithValidate conditions={licensePlateConditions} required label="מספר רישוי" value={licensePlate}></InputWithValidate>
        <BasicSelect lable={"בחר חברה"} values={Object.keys(carsDictionary)} setValueToSave={SetCompany}></BasicSelect>
        <BasicSelect disable={!Company} lable={"בחר דגם"} values={Models}></BasicSelect>
        <TextField type="number" required label="מספר מקומות" value={numberOfSeats}></TextField>
        <InputWithValidate conditions={yearBookConditions} required label="שנתון" value={yearBook}></InputWithValidate>
        <FormControlLabel control={<Checkbox checked={automaticGear} />} label={"תיבת הילוכים אוטומטית"}></FormControlLabel>
        <SelectFromJson lable={"בחר סוג הנעה"} values={driveTypes} callReach={(x) => x.description} setValueToSave={setDriveType}></SelectFromJson>
        <TextField type="number" required label="מחיר לשעה" value={pricePerHour}></TextField>
        <TextField type="number" required label="צריכה לקילומטר" value={fuelConsumptionPerKm}></TextField>
        <TextField type="number" required label="יתרה בליטרים" value={balance}></TextField>
        <TextField required label="עיר"></TextField>
        <TextField required label="רחוב"></TextField>
        <Button className="button" type="submit">אישור</Button>
    </>

}
