import { Button, Dialog, DialogContent, DialogContentText, DialogTitle, IconButton, Paper, TextField } from "@mui/material"
import { useDispatch, useSelector } from "react-redux"
import { addLend, updateCarAvailable } from "../redux/Actions"
import CloseIcon from '@mui/icons-material/Close';
import Swal from "sweetalert2"


export const AddLend = ({ id, setShow }) => {

    let car = useSelector(x => x.Cars).find(c => c.id == id)

    let lends = useSelector(x => x.Lends)

    const user = useSelector(x => x.CurrentUser)

    const dis = useDispatch()

    let idLend = parseInt(lends[lends.length - 1].id) + 1485

    const lend = (e) => {
        e.preventDefault();

        let lend =
        {
            id: parseInt(idLend),
            userId: parseInt(user.id),
            carId: parseInt(id),
            dateAndTime: new Date(),
            returned: false
        }

        dis(addLend(lend, dis))

        dis(updateCarAvailable(id, false))

        setShow(false)

        Swal.fire({
            title: "ההזמנה נקלטה בהצלחה",
            html:
                `
                    <p>הרכב מחכה לך בכתובת: `+ car.street + " " + car.city + `</p>
                    <p>מספר הזמנה: `+ idLend + `</p>
                `,
            icon: "success",
            timer: 20000,
            position: "center",
            timerProgressBar: true,
        })
        console.log(lends);
        
    }

    if (user)
        return (
            <>
                <Dialog open={true}
                    id="DialogLendMain"
                    PaperProps={{
                        component: 'form',
                        onSubmit: (e) => { lend(e) },
                    }}>
                    <DialogTitle id="closeDialog">
                        <IconButton className="roundButton" onClick={() => setShow(false)} ><CloseIcon /></IconButton>
                        {user && <DialogContentText id="carDetailes"> פרטי השכרה: </DialogContentText>}
                    </DialogTitle>
                    <DialogContent id="dialogLend" >
                        <TextField className="dialogForm" label="מספר רישוי" value={car.licensePlate} aria-readonly></TextField>
                        <TextField className="dialogForm" label="תאריך" value={new Date().toLocaleDateString()} aria-readonly></TextField>
                        <TextField className="dialogForm" label="שעה" value={new Date().toLocaleTimeString()} aria-readonly></TextField>
                        <Button id="submitLend" type="submit">אישור</Button>
                    </DialogContent>
                </Dialog>
            </>
        )
    else {
        setShow(false)
        Swal.fire({
            icon: "error",
            text: "ההשכרה למנויים בלבד"
        })
        return <></>
    }
}
