import { useSelector } from "react-redux"
import * as React from 'react';
import Table from '@mui/material/Table';
import TableBody from '@mui/material/TableBody';
import TableCell from '@mui/material/TableCell';
import TableContainer from '@mui/material/TableContainer';
import TableHead from '@mui/material/TableHead';
import TableRow from '@mui/material/TableRow';
import Paper from '@mui/material/Paper';
import { Button, Dialog, DialogActions, DialogContent, DialogContentText, DialogTitle, IconButton } from "@mui/material";
import CloseIcon from '@mui/icons-material/Close';
import { useNavigate } from "react-router";
import { DriveType } from "./DriveType";


export const DriveTypes = () => {

    let driveTypes = useSelector(x => x.DriveTypes)

    // alert(JSON.stringify(driveTypes))

    let nav = useNavigate()

    //    DriveTypes: [{ id: 1, description: "דלק", pricePerLiter: 10 },

    /*
    let remove = (id) => {
        let des = driveTypes.find(t => t.id == id).description
        alert("אני רוצה למחוק את " + des)
    }

    let edit = (id) => {
        let des = driveTypes.find(t => t.id == id).description
        alert("אני רוצה לערוך את " + des)
    }*/

    let [add, setAdd] = React.useState(false)

    return <>

        <Dialog open={true}
            id="DialogDriveTypes"
        >
            <DialogTitle id="closeDialog">
                <IconButton className="roundButton" onClick={() => nav("../")} ><CloseIcon /></IconButton>
                <DialogContentText id="carDetailes">סוגי הנעה: </DialogContentText>
            </DialogTitle>
            <DialogContent id="DialogTypes">
                <TableContainer id="driveTypesTable" component={Paper} >
                    <Table sx={{ minWidth: 500 }} aria-label="simple table">
                        <TableHead>
                            <TableRow>
                                <TableCell id="rows" align="center">סוג הנעה</TableCell>
                                <TableCell id="rows" align="center">מחיר לשעה</TableCell>
                                <TableCell id="rows" align="center"></TableCell>
                                <TableCell id="rows" align="center"></TableCell>
                            </TableRow>
                        </TableHead>
                        <TableBody>
                            {driveTypes.map((t) => (
                                /*
                                <TableRow
                                    key={t.description}
                                    sx={{ '&:last-child td, &:last-child th': { border: 0 } }} >
                                    <TableCell component="th" scope="row" align="center" id="rows"> {t.description} </TableCell>
                                    <TableCell align="center" id="rows">{t.pricePerLiter}</TableCell>
                                    <TableCell align="center" id="rows"> <IconButton className="roundButton" onClick={() => edit(t.id)} aria-label="delete"><EditIcon /></IconButton> </TableCell>
                                    <TableCell align="center" id="rows"> <IconButton className="roundButton" onClick={() => remove(t.id)} aria-label="delete"><DeleteIcon /></IconButton> </TableCell>
                                </TableRow>
                                */
                               <DriveType description={t.description} id={t.id} pricePerLiter={t.pricePerLiter}></DriveType>
                            ))}
                        </TableBody>
                    </Table>
                </TableContainer>
            </DialogContent>
            <DialogActions id='dialogFunctions'>
                    <div className='DialogActions'>
                        <Button className="button" variant="contained" onClick={()=>add()} > להוספה </Button>
                    </div>
                </DialogActions>
        </Dialog>

    </>
}
