import { useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import { useNavigate } from "react-router";
import { addModel } from "../redux/Actions";
import { Dialog, DialogContent, DialogContentText, DialogTitle, IconButton, TextField } from "@mui/material";

export const AddModel = () => {

    const [selectedFile, setSelectedFile] = useState();

    const [CarType, setCarType] = useState()

    let CarTypes = useSelector(x => x.CarTypes)

    let CarsModels = useSelector(x => x.CarModels)

    let dis = useDispatch()

    let nav = useNavigate()


    const fileChange = (event) => {
        const file = event.target.files[0];
        if (file) {
            setSelectedFile(URL.createObjectURL(file));
        }
    };

    const send = (e) => {
        e.preventDefault();
        // Here you can handle the file upload, e.g. sending it to a server
        // alert(selectedFile);

        let model =
        {
            id: parseInt(CarsModels[CarsModels.length - 1].id) + 1,
            company: e.target[0].value,
            model: e.target[2].value,
            carTypeId: CarType,
            img: selectedFile
        }

        dis(addModel(model))

        console.log(model);

        nav("../")

    };

    return <>
        <Dialog open={true}
            id="DialogUpdateCar"
        >
            <DialogTitle id="closeDialog">
                <IconButton className="roundButton" onClick={() => nav("../")} ><CloseIcon /></IconButton>
                <DialogContentText id="carDetailes">הוספת רכב: </DialogContentText>
            </DialogTitle>
            <DialogContent id="DialogUpdate">
                <form onSubmit={(e) => send(e)}>
                    <TextField label="company"></TextField>
                    <TextField label="model name"></TextField>
                    <SelectFromJson lable={"בחר סוג רכב"} values={CarTypes} callReach={(x) => x ? x.description : ""} callSave={(x) => x ? x.id : ""} setValueToSave={setCarType}></SelectFromJson>
                    <TextField type="file" accept=".png" onChange={fileChange} />
                    <Button type="submit">אישור</Button>
                </form>
            </DialogContent>
        </Dialog>

    </>
}
