export const addUser = (user) => {
    return { type: 'ADD_USER', payload: user }
}

export const addCar = (car) => {
    return { type: 'ADD_CAR', payload: car }
}

export const updateCar = (id, car) => {
    return { type: 'UPDATE_CAR', payload: { id, car } }
}

export const addLend = (lend) => {
    return { type: 'ADD_LEND', payload: lend }
}

export const addReturn = (ret) => {
    return { type: 'ADD_RETURN', payload: ret }
}

export const updateReturn = (id) => {
    return { type: 'UPDATE_RETURN', payload: id }
}

export const updateCarAvailable = (id, available) => {
    return { type: 'UPDATE_CAR_AVAILABLE', payload: { id, available } }
}

export const removeCar = (id) => {
    return { type: 'REMOVE_CAR', payload: id }
}

export const updateDriveTypePrice = (id, newPrice) => {
    return { type: 'UPDATE_DRIVE_TYPE_PRICE', payload: { id, newPrice } }
}

export const setCurrentUser = (user) => {
    return { type: 'SET_CURRENT_USER', payload: user }
}

export const addModel = (model, dis) => {
    return { type: 'ADD_MODEL' , payload: {model, dis} }
}

export const addDriveType = (driveType) => {
    return { type: 'ADD_DRIVE_TYPE', payload: driveType }
}

export const removeDriveType = (id) => {
    return { type: 'REMOVE_DRIVE_TYPE', payload: id }
}

export const setCarModels = (list) => {
    return { type: 'SET_CAR_MODEL', payload: list }
}

export const setCarTypes = (list) => {
    return { type: 'SET_CAR_MODEL', payload: list }
}
