import { Autocomplete, Button, MenuItem, Select, TextField } from "@mui/material";
import AddCircleIcon from '@mui/icons-material/AddCircle';
import { useEffect, useRef, useState } from "react";
import AddBoxIcon from '@mui/icons-material/AddBox';
import axios from "axios";
import CloseIcon from '@mui/icons-material/Close';
import FreeSoloCreateOptionDialog from "./Aaa";

export const InputApartmentDetailes = ((onSubmit = (e) => { console.log(e); }) => {


    //  name, description, imgUrl, category, city, address, countOfBeds, advertiser, plugins

    const [plugins, setPlugins] = useState([])

    const [openPlugin, setOpenPlugin] = useState(false)

    const pluginsToShow = plugins.join(', ')

    const pluginToAdd = useRef()

    let [categories, setCategories] = useState([])

    let [cities, setCities] = useState([])

    let [categoryId, setCategoryId] = useState(null)

    let [cityId, setCityId] = useState(null)


    const handleCityChange = (event, value) => {
        if (value) {
            if (value._id === null) {
                // אם נבחרה אופציה חדשה, הוספת העיר לרשימה
                const newCity = { name: value.name, _id: null };
                setCities([...cities, newCity]);
                setCityId(null); // או לשמור את ה-ID של העיר החדשה אם תרצה
            } else {
                setCityId(value._id); // שמירה של ה-ID של העיר שנבחרה
            }
        } else {
            setSelectedCityId(null); // אם לא נבחרה עיר
        }
    };

    const getOptionLabel = (option) => {
        if (option._id === null) {
            return `הוספת ${option.name} לרשימה`;
        }
        return option.name;
    };

    useEffect(() => {
        axios.get("/city/all")
            .then((x) => {
                setCities(x.data)
            })
            .catch((err) => {
                console.log(err.message);
            })

        axios.get("/category")
            .then((x) => {
                setCategories(x.data)
            })
            .catch((err) => {
                console.log(err.message);
            })

    }, [])


    return (
        <>
            <form className="form" onSubmit={(e) => {
                e.preventDefault()
                alert(categoryId)
                alert(e.target[4].value);

                // onSubmit(e)
            }}>
                <TextField label="שם" variant="outlined"></TextField>
                <TextField label="תיאור" variant="outlined"></TextField>
                {/* תמונה */}
                <FreeSoloCreateOptionDialog></FreeSoloCreateOptionDialog>
                <Autocomplete
                    freeSolo
                    disablePortal
                    options={categories}
                    getOptionLabel={(options) => options.name}
                    getOptionKey={(options) => options._id}
                    // sx={{ width: 300 }}
                    onChange={(e, value) => {
                        alert(value);
                        if (value) setCategoryId(value._id);
                        else setCategoryId(null)
                    }}
                    renderInput={(params) => <TextField {...params} label="קטגוריה" />}
                />
                {/* <Autocomplete
                    freeSolo
                    disablePortal
                    options={cities}
                    getOptionLabel={(options) => options.name}
                    getOptionKey={(options) => options._id}
                    // sx={{ width: 300 }}
                    renderInput={(params) => <TextField {...params} label="עיר" />}
                /> */}
                <Autocomplete
                    freeSolo
                    disablePortal
                    options={cities}
                    getOptionLabel={getOptionLabel}
                    onChange={handleCityChange}
                    renderInput={(params) => <TextField {...params} label="עיר" />}
                    onInputChange={(event, value) => {
                        if (value && !cities.some(city => city.name === value)) {
                            // אם העיר לא קיימת, הוסף אופציה חדשה עם ID null
                            const newOption = { name: value, _id: null };
                            setCities(prevCities => [...prevCities, newOption]);
                        }
                    }}
                />
                <TextField label="כתובת" variant="outlined"></TextField>
                <TextField type="number" label="מספר מיטות" variant="outlined"></TextField>
                <p>{pluginsToShow}</p>
                <Button startIcon={<AddBoxIcon></AddBoxIcon>} onClick={() => setOpenPlugin(true)}>הוספת תוסף</Button>
                {openPlugin && <>
                    <div>
                        <Button startIcon={<CloseIcon></CloseIcon>} variant="outlined" onClick={() => { setOpenPlugin(false) }}></Button>
                        <TextField variant="outlined" ref={pluginToAdd}></TextField>
                        <Button variant="outlined" onClick={() => {
                            setPlugins([...plugins, pluginToAdd.current.children[0].children[0].value])
                            setOpenPlugin(false)
                        }}>הוספה</Button>
                    </div>
                </>}
                <Button startIcon={<AddCircleIcon></AddCircleIcon>} className="inputTextField" id="submitInput" type="submit" variant="outlined">הוספה</Button>
            </form >
        </>
    )
})
