import { Button, IconButton } from "@mui/material"
import LocationOnIcon from '@mui/icons-material/LocationOn'
import DeleteIcon from '@mui/icons-material/Delete'
import EditIcon from '@mui/icons-material/Edit';
import { useDispatch, useSelector } from "react-redux"
import { useNavigate } from "react-router"
import { removeCar } from "../redux/Actions";
import { useState } from "react";
import { CarDetails } from "./CarDetails";
import { AddLend } from "./AddLend";
import Swal from "sweetalert2";

export const Car = (props) => {

    const { id, licensePlate, carModelId, NumberOfSeats, img, yearBook, AutomaticGear, driveTypeId, pricePerHour, fuelConsumptionPerKm, balance, city, street, available } = props.car

    const carModel = useSelector(x => x.CarModels).find(m => m.id == carModelId)
    const user = useSelector(x => x.CurrentUser)

    const [showDet, setShowDet] = useState(false)
    const [showLend, setShowLend] = useState(false)

    let userTypes = useSelector(x => x.UserTypes)

    const isManager = user && user.userTypeId == userTypes.find(ut => ut.description == "manager").id

    const nav = useNavigate()
    const dis = useDispatch()

    const showDetails = (e) => {
        // nav(`/carDetails/${id}`)
        if (available)
            nav("/InputCarDetailes")
        else
            Swal.fire({
                icon: "error",
                text: "לא ניתן לערוך רכב מושאל",
                showConfirmButton: false,
                timer: 2000
            })
    }

    const remove = () => {

        if (available)
            Swal.fire({
                title: "האם אתה בטוח שאתה רוצה למחוק את הרכב?",
                showDenyButton: true,
                confirmButtonText: "אישור",
                denyButtonText: `ביטול`
            }).then((result) => {
                if (result.isConfirmed) {
                    Swal.fire({
                        icon: "success",
                        text: "",
                        showConfirmButton: false,
                        timer: 2000
                    })
                    dis(removeCar(id))

                }
            });
        else
        Swal.fire({
            icon: "error",
            text: "לא ניתן למחוק רכב מושאל",
            showConfirmButton: false,
            timer: 2000
        })

    }

    const edit = () => {
        // nav(`/updateCar/${id}`)
        if (available)
            nav("/InputCarDetailes")
        else
            Swal.fire("לא ניתן לערוך רכב מושאל", "", "error");
    }

    return <div className="car">
        <div className="header">
            <div className="available" style={{ backgroundColor: available ? "green" : "red" }}>{available ? "פנוי" : "תפוס"}</div>
            {isManager && <div className="managerOptions">
                <IconButton className="roundButton" onClick={edit} aria-label="delete"><EditIcon /></IconButton>
                <IconButton className="roundButton" onClick={remove} aria-label="delete"><DeleteIcon /></IconButton>
            </div>}
        </div>
        <img src={`${process.env.PUBLIC_URL}/images/cars/${img}`} alt={img} onClick={() => { setShowDet(true) }}></img>
        {showDet && <CarDetails id={id} setShow={setShowDet} setLend={setShowLend}></CarDetails>}
        <p>{carModel.company} {carModel.model}</p>
        <LocationOnIcon></LocationOnIcon> <label>{city}</label><br></br>
        <Button onClick={() => setShowLend(true)} className="button" variant="contained" disabled={!available} > להשכרה </Button>
        {showLend && <AddLend id={id} setShow={setShowLend}></AddLend>}

    </div>
}
