import { Box, FormControl, InputLabel, MenuItem, Select } from "@mui/material";
import { useEffect, useState } from "react"

export const SelectFromJson = ({ lable, values, setValueToSave = () => { }, callReach, value = null }) => {

    // alert(callReach(value))

    const [valueInput, setValueInput] = useState()

    
    useEffect(() => {
        setValueInput("rtrtrttr")
    }, [])
    // 

    const changeValue = (event) => {
        alert(JSON.  event.target.value)
        setValueInput(event.target.value)
        setValueToSave(event.target.value)
    }

    console.log(values);


    return <>
        <Box className="select" sx={{ minWidth: 140 }}>
            <FormControl fullWidth>
                <InputLabel id="demo-simple-select-label">{lable}</InputLabel>
                <Select
                    labelId="demo-simple-select-label"
                    // id="demo-simple-select"
                    value={valueInput}
                    label={lable}
                    onChange={(e) => changeValue(e)}
                >
                    {values.map((x) => {
                        if (callReach(x) != callReach(value))
                            return <MenuItem value={x}>{callReach(x)}</MenuItem>
                        else
                            // alert(callReach(x));
                        return <MenuItem value={x} selected={true}>{callReach(x)}</MenuItem>
                    }
                    )}
                </Select>
            </FormControl>
        </Box>
    </>

}
