import { TextField } from "@mui/material"
import { useTzValidate } from "../MyHooks/UseTzValidate"
import { usePhoneValidate } from "../MyHooks/UsePhoneValidate"
import { usePsswordValidate } from "../MyHooks/UsePasswordValidate"
import { useValidate } from "../MyHooks/UseValidate"

export const Register = () => {

    let tzVal = useTzValidate()

    let phoneVal = usePhoneValidate()

    let passwordVal = usePsswordValidate()

    let creditNumberVal = useValidate(
        [
            [{func:(value)=> !/^\d{16}$/.test(value), messege:"invalid credit number"}]
        ]

    )

    let validitiVal = useValidate(
        [{func:(value)=> !/^((0[1-9])|(1[0-2]))-\d{2}$/.test(value), messege:"invalid validiti"}]
    )

    let cvvVal = useValidate(
        [{func:(value)=> !/^\d{3}$/.test(value), messege:"invalid cvv"}]
    )

    return <>
        <TextField id="tzInput" label="identity" variant="outlined" error={tzVal.tzErrorDisplay} onBlur={(e) => tzVal.checkTz(e)} helperText={tzVal.tzError} required />
        <TextField id="nameInput" label="user name" variant="outlined"></TextField>
        <TextField id="passwordInput" type="password" label="password" variant="outlined" error={passwordVal.passwordErrorDisplay} helperText={passwordVal.passwordError} required/>
        <TextField id="phoneInput" type="tel" label="phone" variant="outlined" error={phoneVal.phoneErrorDisplay} onBlur={(e) => phoneVal.checkPhone(e)} helperText={phoneVal.phoneError} />
        <TextField id="creditNumberInput" label="credit number" variant="outlined" error={creditNumberVal.errorDisplay} onBlur={(e) => creditNumberVal.check(e)} helperText={creditNumberVal.error} required></TextField>
        <TextField id="validityInput" label="validity" variant="outlined" error={validitiVal.errorDisplay} onBlur={(e) => validitiVal.check(e)} helperText={validitiVal.error} required></TextField>
        <TextField id="cvvInput" label="cvv" variant="outlined" error={cvvVal.errorDisplay} onBlur={(e) => cvvVal.check(e)} helperText={validitiVal.error} required></TextField>
        <TextField id="submitInput" type="submit" variant="outlined" />
        </>
}
