import { TextField } from "@mui/material"
import { useState } from "react"

export const InputWithValidate = ({ conditions = [], className = "",type="texe", id = "", label = "", errorMessege = "", required = false }) => {

    const [error, setError] = useState()
    const [errorDisplay, setErrorDisplay] = useState(false)
/*
    console.log(id + "   " + errorMessege);
    console.log(id + "   " + error);
*/
    const check = (e) => {
        let i
        for (i = 0; i < conditions.length && !conditions[i].func(e.target.value); i++);

        if (i == conditions.length) {
            setError("")
            setErrorDisplay(false)
        }

        else {
            setError(conditions[i].messege)
            setErrorDisplay(true)
            e.target.focus()
        }

    }

    return <TextField type={type} className={className} id={id} label={label} variant="outlined" error={errorDisplay || (errorMessege!=null && errorMessege!="")} onBlur={(e) => {check(e); console.log(e)}} helperText={error || errorMessege} required={required} />

}
