import { Box, FormControl, InputLabel, MenuItem, Select } from "@mui/material";
import { useState } from "react"

// };
export const BasicSelect = ({ lable, values, setValueToSave = ()=>{} }) => {

    const [value, setValue] = useState()

    const changeValue = (event) => {
        setValue(event.target.value);
        setValueToSavevalueToSave = value
    }

    return <>
        <Box className="select" sx={{ minWidth: 140 }}>
            <FormControl fullWidth>
                <InputLabel id="demo-simple-select-label">{lable}</InputLabel>
                <Select
                    labelId="demo-simple-select-label"
                    // id="demo-simple-select"
                    value={value}
                    label={lable}
                    onChange={changeValue}

                >
                    {values.map((x) => <MenuItem value={x}>{x}</MenuItem>)}
                </Select>
            </FormControl>
        </Box>
    </>

}
