export const addCar = (car, dis) => {
    return { type: 'ADD_CAR', payload: { car, dis } }
}

export const updateCar = (id, car, dis) => {
    return { type: 'UPDATE_CAR', payload: { id, car, dis } }
}

export const addLend = (lend, dis, street, city) => {
    return { type: 'ADD_LEND', payload: { lend, dis, street, city } }
}

export const updateCarAvailable = (id, dis) => {
    return { type: 'UPDATE_CAR_AVAILABLE', payload: { id, dis } }
}

export const removeCar = (id, dis) => {
    return { type: 'REMOVE_CAR', payload: { id, dis } }
}

export const updateDriveTypePrice = (id, newPrice, dis) => {
    return { type: 'UPDATE_DRIVE_TYPE_PRICE', payload: { id, newPrice, dis } }
}

export const setCurrentUser = (user) => {
    return { type: 'SET_CURRENT_USER', payload: user }
}

export const addModel = (model, dis) => {
    return { type: 'ADD_MODEL', payload: { model, dis } }
}

export const addDriveType = (driveType, dis) => {
    return { type: 'ADD_DRIVE_TYPE', payload: { driveType, dis } }
}

export const removeDriveType = (id, dis) => {
    return { type: 'REMOVE_DRIVE_TYPE', payload: { id, dis } }
}

export const setCarModels = (list) => {
    return { type: 'SET_CAR_MODEL', payload: list }
}

export const setCarTypes = (list) => {
    return { type: 'SET_CAR_TYPES', payload: list }
}

export const setDriveType = (list) => {
    return { type: 'SET_DRIVE_TYPES', payload: list }
}

export const setRentals = (list) => {
    return { type: 'SET_LENDS', payload: list }
}

export const setCars = (list) => {
    return { type: 'SET_CARS', payload: list }
}


export const addUser = (user) => async (dis) => {
    try {
        const response = await addNewUser(user)
        dis({ type: 'ADD_USER', payload: response.data })
    } catch (error) {
        console.error("Error post user:", error);
    }

}

export const setDriveTypes = () => async (dis) => {
    try {
        const response = await getDriveTypes()
        dis({ type: 'SET_DRIVE_TYPES', payload: response.data })
    } catch (error) {
        console.error("Error fetching drive types:", error);
    }
}

export const addReturn = (ret) => async (dis) => {
    try {
        const response = await addReturnCar(ret)
        dis({ type: 'SET_RETURNS', payload: response.data })
    } catch (error) {
        console.error("Error fetching returns:", error);
    }
}
