import { Margin, Padding } from "@mui/icons-material"
import { Dialog, DialogContent, TextField } from "@mui/material"
import { useDispatch, useSelector } from "react-redux"
import { useParams } from "react-router"

export const AddLend = ({ id, setShow }) => {

    let car = useSelector(x => x.Cars).find(c => c.id == id)

    let lends = useSelector(x=>x.Lends)

    const user = useSelector(x => x.CurrentUser)

    const dis = useDispatch()

    const lend = (e) => {
        e.preventDefault();

        let lend=
        {
            id:parseInt(lends[lends.length-1].id)+1,
            userId:user.id,
            carId:id,
            date: e.target[2].value,
            time: e.target[4].value
        }



        console.log(lend);
        



        setShow(false)
    }

    return (
        <Dialog open={true}
            PaperProps={{
                component: 'form',
                onSubmit: (e) => { lend(e) },
            }}>
            <DialogContent id="dialogLend" >
                <TextField className="dialogForm" label="מספר רישוי" value={car.licensePlate} aria-readonly></TextField>
                <TextField className="dialogForm" label="תאריך" value={new Date().toLocaleDateString()} aria-readonly></TextField>
                <TextField className="dialogForm" label="שעה" value={new Date().toLocaleTimeString()} aria-readonly></TextField>
                <TextField id="submitLend" value="להשכרה" type="submit"></TextField>
            </DialogContent>
        </Dialog>
    )
}
