import { Button, TextField } from "@mui/material";
import AddCircleIcon from '@mui/icons-material/AddCircle';
import { useEffect, useRef, useState } from "react";
import AddBoxIcon from '@mui/icons-material/AddBox';
import axios from "axios";
import CloseIcon from '@mui/icons-material/Close';
import Autocomplete, { createFilterOptions } from '@mui/material/Autocomplete';
import DeleteIcon from '@mui/icons-material/Delete'

const filter = createFilterOptions();


export const InputApartmentDetailes = ((onSubmit = (e) => { console.log(e); }) => {

    const [plugins, setPlugins] = useState([])

    const [openPlugin, setOpenPlugin] = useState(false)

    const pluginToAdd = useRef()

    let [categories, setCategories] = useState([])

    let [cities, setCities] = useState([])

    const [city, setCity] = useState(null);

    const saveNewCity = (newValue) => {
        let newCity = { name: newValue.inputValue, _id: -1 }
        setCities([...cities, newCity])
        setCity(newValue.inputValue);
    }

    const [category, setCategory] = useState(null);

    const saveNewCategory = (newValue) => {
        let newCategory = { name: newValue.inputValue, _id: -1 }
        setCategories([...categories, newCategory])
        setCategory(newValue.inputValue);
    }

    useEffect(() => {
        axios.get("/city/all")
            .then((x) => {
                setCities(x.data)
            })
            .catch((err) => {
                console.log(err.message);
            })

        axios.get("/category")
            .then((x) => {
                setCategories(x.data)
            })
            .catch((err) => {
                console.log(err.message);
            })

    }, [])


    return (
        <>
            <form className="form" onSubmit={(e) => {
                e.preventDefault()
                alert(e.target[4].value);
                // onSubmit(e)
            }}>
                <TextField label="שם" variant="outlined"></TextField>
                <TextField label="תיאור" variant="outlined"></TextField>
                {/* תמונה */}
                <Autocomplete
                    value={category}
                    onChange={(event, newValue) => {
                        if (typeof newValue === 'string') {
                            setTimeout(() => {
                                saveNewCategory(newValue)
                            });
                        } else if (newValue && newValue.inputValue) {
                            saveNewCategory(newValue)
                        } else {
                            setCategory(newValue);
                        }
                    }}
                    filterOptions={(options, params) => {
                        const filtered = filter(options, params);

                        if (params.inputValue !== '') {
                            filtered.push({
                                inputValue: params.inputValue,
                                name: `הוספת ${params.inputValue} לרשימה`,
                            });
                        }

                        return filtered;
                    }}
                    id="free-solo-dialog-demo"
                    options={categories}
                    getOptionLabel={(option) => {
                        if (typeof option === 'string') {
                            return option;
                        }
                        if (option.inputValue) {
                            return option.inputValue;
                        }
                        return option.name;
                    }}
                    selectOnFocus
                    clearOnBlur
                    handleHomeEndKeys
                    renderOption={(props, option) => {
                        const { key, ...optionProps } = props;
                        return (
                            <li key={key} {...optionProps}>
                                {option.name}
                            </li>
                        );
                    }}
                    // sx={{ width: 300 }}
                    freeSolo
                    renderInput={(params) => <TextField {...params} label="קטגוריה" />}
                ></Autocomplete>
                <Autocomplete
                    value={city}
                    onChange={(event, newValue) => {
                        if (typeof newValue === 'string') {
                            setTimeout(() => {
                                saveNewCity(newValue)
                            });
                        } else if (newValue && newValue.inputValue) {
                            saveNewCity(newValue)
                        } else {
                            setCity(newValue);
                        }
                    }}
                    filterOptions={(options, params) => {
                        const filtered = filter(options, params);

                        if (params.inputValue !== '') {
                            filtered.push({
                                inputValue: params.inputValue,
                                name: `הוספת ${params.inputValue} לרשימה`,
                            });
                        }

                        return filtered;
                    }}
                    id="free-solo-dialog-demo"
                    options={cities}
                    getOptionLabel={(option) => {
                        if (typeof option === 'string') {
                            return option;
                        }
                        if (option.inputValue) {
                            return option.inputValue;
                        }
                        return option.name;
                    }}
                    selectOnFocus
                    clearOnBlur
                    handleHomeEndKeys
                    renderOption={(props, option) => {
                        const { key, ...optionProps } = props;
                        return (
                            <li key={key} {...optionProps}>
                                {option.name}
                            </li>
                        );
                    }}
                    // sx={{ width: 300 }}
                    freeSolo
                    renderInput={(params) => <TextField {...params} label="עיר" />}
                ></Autocomplete>
                <TextField label="כתובת" variant="outlined"></TextField>
                <TextField type="number" label="מספר מיטות" variant="outlined"></TextField>
                {/* <p>{plugins.join(', ')}</p> */}
                {plugins.length > 0 &&
                    <table>
                        <tbody>
                            {plugins.map((p, i) =>
                                <tr>
                                    <td>{p}</td>
                                    <td><Button startIcon={<DeleteIcon></DeleteIcon>} onClick={() => { setPlugins(prevPlugins => prevPlugins.filter((p, j) => j != i)) }}></Button>                                    </td>
                                </tr>
                            )}
                            {openPlugin &&
                                <tr>
                                </tr>}
                        </tbody>
                    </table>
                }
                <Button startIcon={<AddBoxIcon></AddBoxIcon>} onClick={() => setOpenPlugin(true)}>הוספת תוסף</Button>
                {openPlugin && <>
                    <div>
                        <Button startIcon={<CloseIcon></CloseIcon>} variant="outlined" onClick={() => { setOpenPlugin(false) }}></Button>
                        <TextField variant="outlined" ref={pluginToAdd}></TextField>
                        <Button variant="outlined" onClick={() => {
                            setPlugins([...plugins, pluginToAdd.current.children[0].children[0].value])
                            setOpenPlugin(false)
                        }}>הוספה</Button>
                    </div>
                </>}
                <Button startIcon={<AddCircleIcon></AddCircleIcon>} className="inputTextField" id="submitInput" type="submit" variant="outlined">הוספה</Button>
            </form >
        </>
    )
})
