import { TextField } from "@mui/material"
import { useDispatch, useSelector } from "react-redux"
import { Link, useNavigate } from "react-router-dom"
import swal from "sweet-alert"
import { getUserTypeDetailes, setCurrentUser } from "../redux/Actions"

export const Login = () => {

    /*
    
    let users = useSelector(x => x.users)

    const send = (event) => {
        //שמחפש לעבור לדף אחר submit ביטול ברירת המחדל של
        event.preventDefault();
        const user = {
            email: event.target[0].value,
            password: event.target[1].value,
        }

        let u = users.find(x => x.email == user.email && x.password == user.password)
        if (u) {
            //הפעלת האקשן באמצעות הדיספץ
            dispatch(setCurrentUser(u))
            nav('/home')
        }
        else {
          //  swal("Oopps!", 'login failed! go to registeration', 'error')
            console.log("oooop")
        }

    }
    */

    let users = useSelector(x => x.Users)

    let dis = useDispatch()

    let nav = useNavigate()

    const login = (e) => {
        e.preventDefault()

//כל תגית מורכבת משתי תגיות ולכן הקפיצות הם בשתיים ולא באחד text fieldבגלל שהשתמשנו ב

        let user = users.find(u => u.tz == e.target[0].value)

        if (user) {
            if (user.password != e.target[2].value)
                console.log("password is not correct")
            else if (user.phone != e.target[4].value && e.target[4].value != '')
                console.log("phone number is not correct")
            else {
                dis(setCurrentUser(user))
                let userType = dis(getUserTypeDetailes(user.userTypeId))
                console.log(u);
                
                nav("/"+userType.description+"Zone")
            }
        }
        else
            console.log("user not fount, try to create a new user")

        /*
                user = users.find(u => u.tz == e.target[0].value && (u.phone == e.target[1].value || e.target[1].value == '') && u.password == e.target[1].value)
                if (user) {
                    dis.setCurrentUser(user)
                    nav("/home")
                }
                else {
        
                    if (users.find(u => u.tz == e.target[0].value))
                        sweetAlert("passwordn not correct", "error")
                    else
                        sweetAlert("user not found, create a new user", "error")
                }
                        */

    }

    return <>
        <form onSubmit={(e) => login(e)}>
            <TextField id="tzInput" label="identity" variant="outlined" required />
            <TextField id="passwordInput" type="text" label="password" variant="outlined"  required />
            <TextField id="telInput" type="tel" label="phone" variant="outlined" />
            <TextField id="submitInput" type="submit" variant="outlined" />
            <Link>register</Link>
        </form>
    </>
}
