import { useValidate } from "./UseValidate"

export const usePhoneValidate = () => {

    const phoneCondotions = [
            { func: (value) => /^([0]\d{1,3}[-])?\d{7,10}$/.test(value), messege: "invalid tz" }
        ]

    let valid = useValidate(phoneCondotions)

    let phoneError = valid.error
    let phoneErrorDisplay = valid.errorDisplay
    let checkPhone = valid.check
    let setPhoneError = valid.setError
    let setPhoneErrorDisplay = valid.setErrorDisplay


    return { phoneError, phoneErrorDisplay, checkPhone, setPhoneError, setPhoneErrorDisplay }
}
