import { IconButton, TableCell, TableRow, TextField } from "@mui/material"
import { useDispatch, useSelector } from "react-redux"
import EditIcon from '@mui/icons-material/Edit';
import DeleteIcon from '@mui/icons-material/Delete';
import CheckIcon from '@mui/icons-material/Check';
import { removeDriveType, updateDriveTypePrice } from "../redux/Actions";
import Swal from "sweetalert2";
import { useState } from "react";

export const DriveType = ({ id, description, pricePerLiter, stateType = "reach" }) => {

    // let t = useSelector(x => x.DriveTypes).find(t => t.id == id)

    // let {description, pricePerLiter} = dt

    //    DriveTypes: [{ id: 1, description: "דלק", pricePerLiter: 10 },

    // let  = props

    let dis = useDispatch()

    const [state, setState] = useState(stateType)

    let edit = () => {
        // alert("אני רוצה לערוך את " + description)
        setState("edit")
    }

    let save = () => {
        dis(updateDriveTypePrice(id, pricePerLiter))
        setState("reach")
    }

    let remove = () => {
        Swal.fire({
            title: "האם אתה בטוח שאתה רוצה למחוק את סוג ההנעה?",
            showDenyButton: true,
            confirmButtonText: "אישור",
            denyButtonText: `ביטול`,
        }).then((result) => {
            if (result.isConfirmed) {
                Swal.fire({
                    icon: "success",
                    text: "סוג ההנעה נמחק!",
                    showConfirmButton: false,
                    timer: 2000
                })
                dis(removeDriveType(id))
            }
        });
    }

    return <>
        <TableRow
            key={description}
            sx={{ '&:last-child td, &:last-child th': { border: 0 } }} >
            <TableCell component="th" scope="row" align="center" id="rows">
            {state == "reach" ? description
                    : <TextField type="number" defaultValue={pricePerLiter} onChange={(e) => { description = e.target.value }}></TextField>
                }
            </TableCell>
            <TableCell align="center" id="rows">
                {state == "reach" ? pricePerLiter
                    : <TextField type="number" defaultValue={pricePerLiter} onChange={(e) => { pricePerLiter = e.target.value }}></TextField>
                }
            </TableCell>
            <TableCell align="center" id="rows">
                {state == "reach" ? <IconButton className="roundButton" onClick={() => edit()} aria-label="delete"><EditIcon /></IconButton>
                    :
                    <IconButton className="roundButton" onClick={() => save()} aria-label="delete"><CheckIcon /></IconButton>
                }
            </TableCell>
            <TableCell align="center" id="rows">
                <IconButton className="roundButton" onClick={() => remove()} aria-label="delete"><DeleteIcon /></IconButton>
            </TableCell>
        </TableRow>
    </>
}
