import { TextField } from "@mui/material"
import { useDispatch, useSelector } from "react-redux"
import { Link, useNavigate } from "react-router-dom"
import swal from "sweet-alert"
import { setCurrentUser } from "../redux/Actions"
import { useTzValidate } from "../MyHooks/UseTzValidate"
import { usePhoneValidate } from "../MyHooks/UsePhoneValidate"
import { usePsswordValidate } from "../MyHooks/UsePasswordValidate"
import { InputIdentity } from "./inputs/InputIdentity"



export const Login = () => {

    let users = useSelector(x => x.Users)

    let userTypes = useSelector(x => x.UserTypes)

    let dis = useDispatch()

    let nav = useNavigate()


    let tzVal = useTzValidate()

    let phoneVal = usePhoneValidate()

    let passwordVal = usePsswordValidate()

    const login = (e) => {
        e.preventDefault()

        //כל תגית מורכבת משתי תגיות ולכן הקפיצות הם בשתיים ולא באחד text fieldבגלל שהשתמשנו ב

        let user = users.find(u => u.tz == e.target[0].value)

        if (user) {
            if (user.password != e.target[2].value) {
                passwordVal.setPasswordError("password is not correct")
                passwordVal.setPasswordErrorDisplay(true)
            } else if (user.phone != e.target[4].value && e.target[4].value != '') {
                phoneVal.setPhoneError("phone number is not correct")
                phoneVal.setPhoneErrorDisplay(true)
            } else {
                dis(setCurrentUser(user))
                let userType = userTypes.find(t => t.id == user.userTypeId)
                nav("/" + userType.description + "Zone")
            }
        }
        else {
            tzVal.setTzError("user not fount, try to create a new user")
            tzVal.setTzErrorDisplay(true)
        }
    }

    return <>
        <form onSubmit={(e) => login(e)}>
            {/*
            <TextField className="inputTextField" id="tzInput" label="identity" variant="outlined" error={tzVal.tzErrorDisplay} onBlur={(e) => tzVal.checkTz(e)} helperText={tzVal.tzError} required />
            <TextField className="inputTextField" id="passwordInput" type="password" label="password" variant="outlined" error={passwordVal.passwordErrorDisplay} helperText={passwordVal.passwordError} required />
            <TextField className="inputTextField" id="telInput" type="tel" label="phone" variant="outlined" error={phoneVal.phoneErrorDisplay} onBlur={(e) => phoneVal.checkPhone(e)} helperText={phoneVal.phoneError} />
            */}
            <InputIdentity className="inputTextField" id="tzInput" label="identity"></InputIdentity>
            <TextField className="inputTextField" id="submitInput" type="submit" variant="outlined" value={"login"} />
        </form>
    </>
}
