import Swal from "sweetalert2"
import { setCars } from "../../redux/Actions"
import axios from "./BasicUrl"

export const getCars = async () => {
    return await axios.get(`Cars`)
}

export const postCars = async (car) => {
    return await axios.post(`Cars`, car)
}

export const putCarsAvailable = (id) => {
    return axios.put(`Cars/available?id=${id}`)
}

export const putCars = (id, car) => {
    return axios.put(`Cars?id=${id}`, car)
}

export const removeCars = (id) => {
    return axios.delete(`Cars?id=${id}`)
}

export const resetCars = (dis) => {

    getCars()
        .then(x => {
            dis(setCars(x.data))
        })
        .catch(error => {
            Swal.fire({
                icon: "error",
                text: "חלה שגיאה בעדכון הנתונים, אנא דווחו למנהל המערכת",
                showConfirmButton: false,
                timer: 2000
            })        })

}
