import { Box, FormControl, InputLabel, MenuItem, OutlinedInput, Select } from "@mui/material"
import { InputWithValidate } from "./InputWithValidate"
import { useState } from "react";

const ITEM_HEIGHT = 48;
const ITEM_PADDING_TOP = 8;
const MenuProps = {
    PaperProps: {
        style: {
            maxHeight: ITEM_HEIGHT * 4.5 + ITEM_PADDING_TOP,
            width: 250,
        },
    },
};

export const InputCarDetailes = () => {



    /*
    { 
        id: 1,
        licensePlate: "36-375-97",
        carModelId: 1,
        numberOfSeats: 5,
        img: "Toyota_Corolla-Space.png",
        yearBook: 2017,
        automaticGear: true,
        driveTypeId: 2,
        pricePerHour: 30,
        fuelConsumptionPerKm: 12,
        balance: 30,
        city: "ירושלים",
        street: "שטראוס 25",
        available: false },
    */

    let licensePlateConditions = [
        { func: (value) => !(/^\d{7,8}$/.test(value) || value == ""), messege: "מספר רישוי לא תקין" }
    ]

    const [model, setModel] = useState();

    const changeModelValue = (event) => {
        setModel(event.target.value);
    };


    return <>
        <InputWithValidate conditions={licensePlateConditions} required label="מספר רישוי"></InputWithValidate>

        <Box className="select" id="carModel" sx={{ minWidth: 140 }}>
            <FormControl fullWidth>
                <InputLabel id="demo-simple-select-label">מודל רכב</InputLabel>
                <Select
                    labelId="demo-simple-select-label"
                    // id="demo-simple-select"
                    value={model}
                    label="Model"
                    onChange={changeModelValue}
                >
                    <MenuItem value={"all"}>{'הכל'}</MenuItem>
                    <MenuItem value={2}>2</MenuItem>
                    <MenuItem value={4}>4</MenuItem>
                    <MenuItem value={5}>5</MenuItem>
                    <MenuItem value={7}>7</MenuItem>
                    <MenuItem value={8}>8</MenuItem>
                    <MenuItem value={9}>9</MenuItem>
                    <MenuItem value={16}>16</MenuItem>
                </Select>
            </FormControl>
        </Box>

    </>

}
