import { TextField } from "@mui/material"
import { useDispatch, useSelector } from "react-redux"
import { Link, useNavigate } from "react-router-dom"
import swal from "sweet-alert"
import { setCurrentUser } from "../redux/Actions"
import { useState } from "react"

export const Login = () => {

    let users = useSelector(x => x.Users)

    let userTypes = useSelector(x => x.UserTypes)

    let dis = useDispatch()

    let nav = useNavigate()

    const [tzError, setTzError] = useState()
    const [tzErrorDisplay, setTzErrorDisplay] = useState(false)
    const [passwordError, setPasswordError] = useState()
    const [passwordErrorDisplay, setPasswordErrorDisplay] = useState(false)
    const [phoneError, setPhoneError] = useState()
    const [phoneErrorDisplay, setPhoneErrorDisplay] = useState(false)

    const checkTz = (e) => {
        if (!parseInt(e.target.value)) {
            setTzError("invalid tz")
            e.target.focus()
            setTzErrorDisplay(true)
        } else if (e.target.value.length < 9) {
            setTzError("tz short")
            e.target.focus()
            setTzErrorDisplay(true)
        } else if (e.target.value.length > 9) {
            setTzError("tz long")
            e.target.focus()
            setTzErrorDisplay(true)
        }
        else {
            setTzError("")
            setTzErrorDisplay(false)
        }

    }

    const checkPssword = (e) => {
        
    }

    const checkPhone = (e) => {
        if(/^([0]\d{1,3}[-])?\d{7,10}$/.test(e.target.value))
            {

                setPhoneError("")
                setPhoneErrorDisplay(false)
            }
            else{
                setPhoneError("invalid phone")
                setPhoneErrorDisplay(true)
            }
    }

    const login = (e) => {
        e.preventDefault()

        //כל תגית מורכבת משתי תגיות ולכן הקפיצות הם בשתיים ולא באחד text fieldבגלל שהשתמשנו ב

        let user = users.find(u => u.tz == e.target[0].value)

        if (user) {
            if (user.password != e.target[2].value)
