import { Box, FormControl, InputLabel, MenuItem, Select } from "@mui/material";
import { useState } from "react"

export const SelectFromJson = ({ lable, values, setValueToSave = () => { }, callReach, value = null }) => {

    // alert(JSON.stringify(driveType))

    const [valueInput, setValueInput] = useState(callReach(value))

    const changeValue = (event) => {
        setValueInput(event.target.value);
        setValueToSave(event.target.value)
    }

    console.log(values);


    return <>
        <Box className="select" sx={{ minWidth: 140 }}>
            <FormControl fullWidth>
                <InputLabel id="demo-simple-select-label">{lable}</InputLabel>
                <Select
                    labelId="demo-simple-select-label"
                    // id="demo-simple-select"
                    value={valueInput}
                    label={lable}
                    onChange={(e) => changeValue(e)}
                >
                    {values.map((x) =>
                        <MenuItem value={x}>{callReach(x)}</MenuItem>
                    )}
                </Select>
            </FormControl>
        </Box>
    </>

}
