import { Button, TextField } from "@mui/material"
import { useDispatch, useSelector } from "react-redux"
import { useNavigate } from "react-router-dom"
import swal from "sweet-alert"
import { setCurrentUser } from "../redux/Actions"
import { InputIdentity } from "./inputs/InputIdentity"
import { InputPassword } from "./inputs/InputPassword"
import { InputPhone } from "./inputs/InputPhone"
import { useState } from "react"
import LoginIcon from '@mui/icons-material/Login';

export const Login = () => {

    sessionStorage.setItem("currentUser", JSON.stringify(null))

    let users = useSelector(x => x.Users)

    let userTypes = useSelector(x => x.UserTypes)

    let dis = useDispatch()

    dis(setCurrentUser(""))

    let nav = useNavigate()

    const [identityErrorMessege, setIdentityErrorMessege] = useState()
    const [passwordErrorMessege, setPasswordErrorMessege] = useState()
    const [phoneErrorMessege, setPhoneErrorMessege] = useState()

    const login = (e) => {
        e.preventDefault()

        //כל תגית מורכבת משתי תגיות ולכן הקפיצות הם בשתיים ולא באחד text fieldבגלל שהשתמשנו ב

        let user = users.find(u => u.tz == e.target[0].value)

        if (user) {
            if (user.password != e.target[2].value)
                setPasswordErrorMessege("password is not correct")
            else if (user.phone != phoneClear(e.target[4].value) && e.target[4].value != '')
                setPhoneErrorMessege("phone number is not correct")
            else {
                dis(setCurrentUser(user))
                let userType = userTypes.find(t => t.id == user.userTypeId)
                nav("/" + userType.description + "Zone")
            }
        }
        else
            setIdentityErrorMessege("user not fount, try to create a new user")

    }

    return <>
        <div className="formInput">
            <form id="login" className="inputs" onSubmit={(e) => login(e)}>
                <InputIdentity className="inputTextField" id="tzInput" label="identity" errorMessege={identityErrorMessege} onBlur={(e) => { setIdentityErrorMessege("") }}></InputIdentity>
                <InputPassword className="inputTextField" id="passwordInput" type="password" label="password" errorMessege={passwordErrorMessege} onBlur={(e) => { setPasswordErrorMessege("") }}></InputPassword>
                <InputPhone className="inputTextField" id="phoneInput" type="tel" label="phone" errorMessege={phoneErrorMessege} onBlur={(e) => { setPhoneErrorMessege("") }}></InputPhone>
                <Button startIcon={<LoginIcon></LoginIcon>} className="inputTextField" id="submitInput" type="submit" variant="outlined">log in</Button>
            </form>
        </div>
    </>
}
