import { Margin, Padding } from "@mui/icons-material"
import { Button, Dialog, DialogContent, DialogTitle, IconButton, TextField } from "@mui/material"
import { useDispatch, useSelector } from "react-redux"
import { Await, useParams } from "react-router"
import { addLend, updateCarAvailable } from "../redux/Actions"
import { useState } from "react"
import { LendMessage } from "./inputs/LendMessege"
import CloseIcon from '@mui/icons-material/Close';
import EditIcon from '@mui/icons-material/Edit';

export const AddLend = ({ id, setShow }) => {

    let car = useSelector(x => x.Cars).find(c => c.id == id)

    let lends = useSelector(x => x.Lends)

    const user = useSelector(x => x.CurrentUser)

    const dis = useDispatch()

    const [open, setOpen] = useState(true)

    const [shoeMessege, setShowMessege] = useState(false)

    let idLend = parseInt(lends[lends.length - 1].id) + 1485

    const lend = (e) => {
        e.preventDefault();

        let lend =
        {
            id: idLend,
            userId: user.id,
            carId: id,
            date: e.target[2].value,
            time: e.target[4].value
        }

        dis(addLend(lend))

        dis(updateCarAvailable(id, false))

        setOpen(false)

        setShowMessege(true)
    }

    return (
        <>
            <Dialog open={open}
                PaperProps={{
                    component: 'form',
                    onSubmit: (e) => { lend(e) },
                }}>
                <DialogTitle id="closeDialog">
                    <IconButton className="roundButton" onClick={()=>setShow(false)} ><EditIcon /></IconButton>
                </DialogTitle>
                <DialogContent id="dialogLend" >
                    <TextField className="dialogForm" label="מספר רישוי" value={car.licensePlate} aria-readonly></TextField>
                    <TextField className="dialogForm" label="תאריך" value={new Date().toLocaleDateString()} aria-readonly></TextField>
                    <TextField className="dialogForm" label="שעה" value={new Date().toLocaleTimeString()} aria-readonly></TextField>
                    <TextField id="submitLend" value="להשכרה" type="submit"></TextField>
                </DialogContent>
            </Dialog>
            {shoeMessege && <LendMessage idCar={id} idLend={idLend} setShowMy={setShowMessege} setShowFather={setShow}></LendMessage>}
        </>
    )
}
