import { Button, Table, TableCell, TableRow, TextField } from "@mui/material";
import AddCircleIcon from '@mui/icons-material/AddCircle';
import { useEffect, useRef, useState } from "react";
import AddBoxIcon from '@mui/icons-material/AddBox';
import axios from "axios";
import CloseIcon from '@mui/icons-material/Close';
import Autocomplete, { createFilterOptions } from '@mui/material/Autocomplete';
import DeleteIcon from '@mui/icons-material/Delete'
import CheckIcon from '@mui/icons-material/Check';
import Swal from "sweetalert2";

const filter = createFilterOptions();

// לבדוק איך צריך להכניס את הברירת מחדל של autocomplete

export const InputApartmentDetailes = ({
    save = (apartment) => { console.log(apartment); },
    nameDefault = null,
    desciptionDefault = null,
    categoryDefault = null,
    cityDefault = null,
    addressDefault = null,
    countOfBedsDefault = null,
    priceDefault = null,
    pluginsDefault = []
}) => {

    const [plugins, setPlugins] = useState(pluginsDefault)

    const [openPlugin, setOpenPlugin] = useState(false)

    const pluginToAdd = useRef()

    let [categories, setCategories] = useState([])

    let [cities, setCities] = useState([])

    const [city, setCity] = useState(null);

    const saveNewCity = (newValue) => {
        let newCity = { name: newValue, _id: -1 }
        setCities([...cities, newCity])
        setCity(newCity);
    }

    const [category, setCategory] = useState(null);

    const saveNewCategory = (newValue) => {
        let newCategory = { name: newValue, _id: -1 }
        setCategories([...categories, newCategory])
        setCategory(newCategory);
    }

    useEffect(() => {
        axios.get("/city/all")
            .then((x) => {
                setCities(x.data)
            })
            .catch((err) => {
                console.log(err.message);
            })

        axios.get("/category")
            .then((x) => {
                setCategories(x.data)
            })
            .catch((err) => {
                console.log(err.message);
            })

    }, [])


    return (
        <>
            <form id="inputApartmentDetailes" className="form" onSubmit={async (e) => {
 
                e.preventDefault()

                if (!category || !city) {
                    Swal.fire({
                        title: !category ? "הכנס קטגוריה" : "הכנס עיר",
                        icon: "error",
                        timer: 700,
                        showConfirmButton: false
                    })
                    return
                }

                let apartment = {
                    name: e.target[0].value,
                    desciption: e.target[2].value,
                    imgUrl: null,
                    category: category,
                    city: city,
                    address: e.target[10].value,
                    countOfBeds: e.target[12].value,
                    price: e.target[14].value,
                    plugins: plugins,
                };

                if (apartment.category._id == -1) {
                    const response = await axios.post("/category", { name: apartment.category.name });
                    apartment.category = response.data._id;
                }

                if (apartment.city._id == -1) {
                    const response = await axios.post("/city", { name: apartment.city.name });
                    apartment.city = response.data._id;
                }

                save(apartment);

            }}>
                <TextField className="inputTextField" required label="שם" variant="outlined" defaultValue={nameDefault}></TextField>
                <TextField className="inputTextField" required label="תיאור" variant="outlined" defaultValue={desciptionDefault}></TextField>
                {/* תמונה */}
                <Autocomplete
                    value={category}
                    defaultValue={categoryDefault}
                    onChange={(event, newValue) => {
                        if (typeof newValue === 'string') {
                            setTimeout(() => {
                                saveNewCategory(newValue.inputValue)
                            });
                        } else if (newValue && newValue.inputValue) {
                            saveNewCategory(newValue.inputValue)
                        } else {
                            setCategory(newValue);
                        }
                    }}
                    filterOptions={(options, params) => {
                        const filtered = filter(options, params);

                        if (params.inputValue !== '') {
                            filtered.push({
                                inputValue: params.inputValue,
                                name: `הוספת ${params.inputValue} לרשימה`,
                            });
                        }

                        return filtered;
                    }}
                    id="free-solo-dialog-demo1"
                    options={categories}
                    getOptionLabel={(option) => {
                        if (typeof option === 'string') {
                            return option;
                        }
                        if (option.inputValue) {
                            return option.inputValue;
                        }
                        return option.name;
                    }}
                    selectOnFocus
                    clearOnBlur
                    handleHomeEndKeys
                    renderOption={(props, option) => {
                        const { key, ...optionProps } = props;
                        return (
                            <li key={key} {...optionProps}>
                                {option.name}
                            </li>
                        );
                    }}
                    // sx={{ width: 300 }}
                    freeSolo
                    renderInput={(params) => <TextField {...params} label="קטגוריה *" />}
                ></Autocomplete>
                <Autocomplete
                    value={city}
                    onChange={(event, newValue) => {
                        if (typeof newValue === 'string') {
                            setTimeout(() => {
                                saveNewCity(newValue.inputValue)
                            });
                        } else if (newValue && newValue.inputValue) {
                            saveNewCity(newValue.inputValue)
                        } else {
                            setCity(newValue);
                        }
                    }}
                    filterOptions={(options, params) => {
                        const filtered = filter(options, params);

                        if (params.inputValue !== '') {
                            filtered.push({
                                inputValue: params.inputValue,
                                name: `הוספת ${params.inputValue} לרשימה`,
                            });
                        }

                        return filtered;
                    }}
                    id="free-solo-dialog-demo2"
                    options={cities}
                    getOptionLabel={(option) => {
                        if (typeof option === 'string') {
                            return option;
                        }
                        if (option.inputValue) {
                            return option.inputValue;
                        }
                        return option.name;
                    }}
                    selectOnFocus
                    clearOnBlur
                    handleHomeEndKeys
                    renderOption={(props, option) => {
                        const { key, ...optionProps } = props;
                        return (
                            <li key={key} {...optionProps}>
                                {option.name}
                            </li>
                        );
                    }}
                    // sx={{ width: 300 }}
                    freeSolo
                    renderInput={(params) => <TextField {...params} label="עיר *" />}
                     className="inputTextField"
                ></Autocomplete>
                <TextField className="inputTextField" required label="כתובת" variant="outlined" defaultValue={addressDefault}></TextField>
                <TextField className="inputTextField" required type="number" label="מספר מיטות" variant="outlined" defaultValue={countOfBedsDefault}></TextField>
                <TextField className="inputTextField" required type="number" label="מחיר" variant="outlined" defaultValue={priceDefault}></TextField>
                {/* <p>{plugins.join(', ')}</p> */}
                {(plugins.length > 0 || openPlugin) &&
                    <Table>
                        <tbody>
                            {plugins.map((p, i) =>
                                <TableRow>
                                    <TableCell>{p}</TableCell>
                                    <TableCell><Button startIcon={<DeleteIcon></DeleteIcon>} onClick={() => { setPlugins(prevPlugins => prevPlugins.filter((p, j) => j != i)) }}></Button>                                    </TableCell>
                                </TableRow>
                            )}
                            {openPlugin &&
                                <TableRow>
                                    <TableCell><TextField variant="outlined" ref={pluginToAdd}></TextField></TableCell>
                                    <TableCell>
                                        <Button variant="outlined" startIcon={<CheckIcon></CheckIcon>} onClick={() => {
                                            setPlugins([...plugins, pluginToAdd.current.children[0].children[0].value])
                                            setOpenPlugin(false)
                                        }}></Button>
                                    </TableCell>
                                </TableRow>}
                        </tbody>
                    </Table>
                }
                <Button startIcon={<AddBoxIcon></AddBoxIcon>} onClick={() => setOpenPlugin(true)}>הוספת תוסף</Button>
                <Button startIcon={<AddCircleIcon></AddCircleIcon>} className="inputTextField" id="submitInput" type="submit" variant="outlined">הוספה</Button>
            </form >
        </>
    )
}
