import { useSelector } from "react-redux"
import TaskAltIcon from '@mui/icons-material/TaskAlt';
import HighlightOffIcon from '@mui/icons-material/HighlightOff';

export const RentalDetails = (props) => {

    const { id, userId, carId, dateAndTime, returned } = props.rental
    const manager = props.manager
    const car = useSelector(x => x.Cars).find(c => c.id == carId)

    console.log("id: "+car.carModelId);
    

    const carModel = useSelector(x => x.CarModels).find(m => m.id == car.carModelId)

    console.log(useSelector(x => x.CarModels));
    

    console.log(carModel);
    

    const user = useSelector(x=>x.Users).find(u=>u.id==userId)
    const datetime = new Date(dateAndTime)

    return <>        
        <img className="carImg" src={carModel.img.indexOf("htt")==-1?`${process.env.PUBLIC_URL}/images/cars/${carModel.img}`: carModel.img} alt={car.img}></img>
        <div className="details">
            <p>{carModel.company} {carModel.model}</p>
            <p>קוד השאלה: {id}</p>
            {manager && 
                <p>לקוח: {user.name}</p>}            
            <p>תאריך: {datetime.toLocaleDateString()}</p>
            <p>שעה: {datetime.toLocaleTimeString()}</p>
            {returned && <div className="flex-row">
                <TaskAltIcon sx={{color:'green'}}></TaskAltIcon>
                <label>הוחזר</label>
            </div>}   
            {!returned && <div className="flex-row">
                <HighlightOffIcon sx={{color:'red'}}></HighlightOffIcon>
                <label>לא הוחזר</label>
            </div>}        
        </div> 
    </>
}
