import { produce } from 'immer';
import { Data } from './Data';
import { postCarModels } from '../ApiRequests/CarModel';
import { setCarModels, setCars, setDriveType, setRentals } from './Actions';
import { postDriveTypes, putDriveType, removeDriveType } from '../ApiRequests/DriveTypes';
import { postRentals, putRentalsReturned } from '../ApiRequests/Rentals';
import { postCars, putCarsAvailable } from '../ApiRequests/Cars';
import Swal from 'sweetalert2';

export const Reducer = produce((state, action) => {

    switch (action.type) {
        case 'ADD_USER':
            state.Users.push(action.payload)
            break;
        case 'ADD_CAR':
            // state.Cars.push(action.payload)
            postCars(action.payload.car)
                .then(x => {
                    action.payload.dis(setCars(x.data))
                })
                .catch(error => {
                    console.log(error.message);

                })
            break;
        case 'UPDATE_CAR':
            let carIndex = state.Cars.findIndex(p => p.id == action.payload.id)
            state.Cars[carIndex] = action.payload.car
            break;
        case 'ADD_LEND':
            // state.Lends.push(action.payload)
            postRentals(action.payload.lend)
                .then(x => {
                    action.payload.dis(setRentals(x.data))
                    console.log(action.payload.messege);
                    // action.payload.messege(x.data[action.data.length - 1].id)
                    Swal.fire({
                        title: "ההזמנה נקלטה בהצלחה",
                        html:
                            `
                                <p>הרכב מחכה לך בכתובת: `+ action.payloadstreet + " " + city + `</p>
                                <p>מספר הזמנה: `+ id + `</p>
                            `,
                        icon: "success",
                        timer: 20000,
                        position: "center",
                        timerProgressBar: true,
                    })
                })
                .catch(error => {
                    console.log(error.message);

                })
            break;
        case 'ADD_RETURN':
            state.Returns.push(action.payload)
            break;
        case 'UPDATE_RETURN':
            // let rentIndex = state.Lends.findIndex(r => r.id == action.payload)
            // state.Lends[rentIndex].returned = true
            putRentalsReturned(parseInt(action.payload.id))
                .then(x => {
                    action.payload.dis(setRentals(x.data))
                })
                .catch(error => {
                    console.log(error.message);

                })
            break;
        case 'UPDATE_CAR_AVAILABLE':
            // let carIndex3 = state.Cars.findIndex(p => p.id == action.payload.id)
            // state.Cars[carIndex3].available = action.payload.available
            putCarsAvailable(parseInt(action.payload.id))
                .then(x => {
                    // alert(JSON.stringify(x.data))
                    action.payload.dis(setCars(x.data))
                })
                .catch(error => {
                    console.log(error.message);
                })
            break;
        case 'REMOVE_CAR':
            let carIndex2 = state.Cars.findIndex(p => p.id == action.payload)
            state.Cars.splice(carIndex2, 1);
            break;
        case 'UPDATE_DRIVE_TYPE_PRICE':
            // let driveType = state.DriveTypes.find(p => p.id == action.payload.id)
            // driveType.pricePerLiter = action.payload.newPrice
            putDriveType(action.payload.id, action.payload.newPrice)
                .then(x => {
                    action.payload.dis(setDriveType(x.data))
                })
                .catch(error => {
                    console.log(error.message);

                })
            break;
        case 'REMOVE_DRIVE_TYPE':
            // let driveType1 = state.DriveTypes.findIndex(p => p.id == action.payload)
            // state.DriveTypes.splice(driveType1, 1);
            removeDriveType(action.payload.id)
                .then(x => {
                    action.payload.dis(setDriveType(x.data))
                })
                .catch(error => {
                    console.log(error.message);

                })
            break
        case 'SET_CURRENT_USER':
            state.CurrentUser = action.payload
            sessionStorage.setItem("currentUser", JSON.stringify(action.payload))
            break;
        case 'ADD_MODEL':
            // state.CarModels.push(action.payload)
            postCarModels(action.payload.model)
                .then(x => {
                    action.payload.dis(setCarModels(x.data))
                })
                .catch(error => {
                    console.log(error.message);

                })

            break;
        case 'ADD_DRIVE_TYPE':
            // state.DriveTypes.push(action.payload)
            postDriveTypes(action.payload.driveType)
                .then(x => {
                    action.payload.dis(setDriveType(x.data))
                })
                .catch(error => {
                    console.log(error.message);

                })
            break;
        case 'UPDATE_CAR_AVAILABLE':
            let carIndex4 = state.Cars.findIndex(p => p.id == action.payload.id)
            state.Cars[carIndex4].available = action.payload.available
            break;
        case 'SET_CAR_MODEL':
            state.CarModels = action.payload
            break;
        case 'SET_CAR_TYPES':
            state.CarTypes = action.payload
            break;
        case 'SET_DRIVE_TYPES':
            state.DriveTypes = action.payload
            break;
        case 'SET_RENTALS':
            state.Lends = action.payload
            break;
        case 'SET_CARS':
            state.Cars = action.payload
            break;

        default:
            break;
    }

}, Data)
