import { TextField } from "@mui/material"
import { useDispatch, useSelector } from "react-redux"
import { Link, useNavigate } from "react-router-dom"
import swal from "sweet-alert"
import { setCurrentUser } from "../redux/Actions"
import { useState } from "react"

export const Login = () => {

    let users = useSelector(x => x.Users)

    let userTypes = useSelector(x => x.UserTypes)

    let dis = useDispatch()

    let nav = useNavigate()

    const [tzError, setTzError] = useState()
    const [passwordError, setPasswordError] = useState()
    const [phoneError, setPhoneError] = useState()
    const [tzErrorDisplay, setTzErrorDisplay] = useState(false)

    const checkTz = (e) => {
        console.log(e.target);
        if (!parseInt(e.target.value)) {
            setTzError("invalid tz")
            e.target.focus()
            setTzErrorDisplay
        } else if (e.target.value.length < 9) {
            setTzError("tz short")
            e.target.focus()
        } else if (e.target.value.length > 9) {
            setTzError("tz long")
            e.target.focus()
        }
        else {
            setTzError("")
        }

    }

    const checkPssword = (e) => {

    }

    const checkPhone = (e) => {

    }

    const login = (e) => {
        e.preventDefault()

        //כל תגית מורכבת משתי תגיות ולכן הקפיצות הם בשתיים ולא באחד text fieldבגלל שהשתמשנו ב

        let user = users.find(u => u.tz == e.target[0].value)

        if (user) {
            if (user.password != e.target[2].value)
                console.log("password is not correct")
            else if (user.phone != e.target[4].value && e.target[4].value != '')
                console.log("phone number is not correct")
            else {
                dis(setCurrentUser(user))
                let userType = userTypes.find(t => t.id == user.userTypeId)
                nav("/" + userType.description + "Zone")
            }
        }
        else
            console.log("user not fount, try to create a new user")

    }

    return <>
        <form onSubmit={(e) => login(e)}>
            <TextField id="tzInput" label="identity" variant="outlined" error={tzErrorDisplay} onBlur={(e) => checkTz(e)} helperText={tzError} required />
            <TextField id="passwordInput" type="text" label="password" variant="outlined" helperText={passwordError}/>
            <TextField id="telInput" type="tel" label="phone" variant="outlined" helperText={phoneError} />
            <TextField id="submitInput" type="submit" variant="outlined"/>
            <Link>register</Link>
        </form>
    </>
}
