import { useSelector } from "react-redux";
import { updateStudentFamilyDetailes } from "../dataManeger/DefiningActions";

export const Login2 = () => {

    let dis = useDispatch()

    let navig = useNavigate()

    let cuu = useSelector()

    //    let product = useSelector(store => store.currentProduct)


    const send = (event) => {

        event.preventDefault();

        /*
        , familyDetailes: { nameFather: 'aaa', nameMother: 'bbb', amountChildren: 5 }
        **/
        dis(updateStudentFamilyDetailes(event.target[0].value,{ nameFather: event.target[0].value, nameMother: event.target[1].value, amountChildren: event.target[2].value}))
        
        navig('/Login2')

    }

    return <>
        <form onSubmit={(e) => send(e)}>
            <label htmlFor={"tz"}>tz: </label>
            <input id="tz"></input>

            <label htmlFor={"name"}>name: </label>
            <input id="name"></input>

            <label htmlFor={"address"}>adderss: </label>
            <input id="address"></input>

            <input type={'submit'} value={'login'}></input><br></br>
        </form>

    </>
}
