import { IconButton, TableCell, TableRow } from "@mui/material"
import { useSelector } from "react-redux"
import EditIcon from '@mui/icons-material/Edit';
import DeleteIcon from '@mui/icons-material/Delete'

export const DriveType = (id) => {

    let t = useSelector(x => x.DriveTypes).find(t => t.id == id)

    // let {description, pricePerLiter} = dt

    //    DriveTypes: [{ id: 1, description: "דלק", pricePerLiter: 10 },


    let edit = (id) => {
        alert("אני רוצה לערוך את " + t.description)
    }

    let remove = (id) => {
        alert("אני רוצה למחוק את " + t.description)
    }

    return <>
        <TableRow
            key={t.description}
            sx={{ '&:last-child td, &:last-child th': { border: 0 } }} >
            <TableCell component="th" scope="row" align="center" id="rows"> {t.description} </TableCell>
            <TableCell align="center" id="rows">{t.pricePerLiter}</TableCell>
            <TableCell align="center" id="rows"> <IconButton className="roundButton" onClick={() => edit(t.id)} aria-label="delete"><EditIcon /></IconButton> </TableCell>
            <TableCell align="center" id="rows"> <IconButton className="roundButton" onClick={() => remove(t.id)} aria-label="delete"><DeleteIcon /></IconButton> </TableCell>
        </TableRow>
    </>
}
